import React, { Component } from 'react'

import {
    CRow,
    CCol,
    CCard,
    CCardHeader,
    CCardBody,
    CFormGroup,
    CLabel,
    CInput,
    CSelect,
    CButton
} from '@coreui/react'

import Pays from '../../components/API/Pays'

class Contact extends Component {
    
    constructor(props){
        super(props)

        this.state = {
            nom : '',
            prenom : '',
            email : '',
            pays : '',
            numero : '',
            periode : '',
            type : '',
        }

        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    validate = () =>{
        let nomEmpty = "";
        let prenomEmpty = "";
        let paysEmpty = "";
        let numeroEmpty = "";
        let periodeEmpty = "";
        let typeEmpty = "";
        let emailEmpty = "";

        /**verification des champs vides */

        if(!this.state.nom){
            nomEmpty =<div class="alert alert-danger" role="alert">Renseignez le nom</div>
        }
        if(!this.state.prenom){
            prenomEmpty = <div class="alert alert-danger" role="alert">Renseignez le prenom</div>
        }
        if(!this.state.pays){
            paysEmpty = <div class="alert alert-danger" role="alert">Renseignez le pays</div>
        }
        if(!this.state.numero){
            numeroEmpty = <div class="alert alert-danger" role="alert">Renseignez le numero</div>
        }
        if(!this.state.type){
            typeEmpty = <div class="alert alert-danger" role="alert">Renseignez le type</div>
        }
        if(!this.state.periode){
            periodeEmpty = <div class="alert alert-danger" role="alert">Renseignez la periode</div>
        }
        if(!this.state.email){
            emailEmpty = <div class="alert alert-danger" role="alert">Renseignez l'email</div>
        }

        /** modifier les valeurs des erreurs*/
        if(emailEmpty){
            this.setState({emailEmpty});
        }
        if(nomEmpty){
            this.setState({nomEmpty});
        }
        if(prenomEmpty){
            this.setState({prenomEmpty});
        }
        if(paysEmpty){
            this.setState({paysEmpty});
        }
        if(numeroEmpty){
            this.setState({numeroEmpty});
        }
        if(typeEmpty){
            this.setState({typeEmpty});
        }

    }

    handleChange(e){
        const name = e.target.name
        this.setState({
            [name] : e.target.value
        })
    }

    handleSubmit(e){
        e.preventDefault()
        const data = JSON.stringify(this.state)
        const isValid = this.validate()
        console.log(data)
    }
    render() {
        return (
                <div className="pl-3 w-100" xs="12">
                    <form onSubmit={this.handleSubmit}>
                    <br />
                    <h2 className="offset-1">Envoyez-nous un message</h2>
                    <CRow>
                        <CCol sm="6" className="offset-1">
                            <span className="text-secondary">
                                Nous répondrons dès que possible. N’hésitez pas à demander conseil ou à nous donner des commentaires
                                sur nos produits ou services
                            <small> Form</small>
                            </span><br /><hr />
                            <CRow>
                                <CCol xs="6">
                                    <CFormGroup>
                                        <CInput value={this.state.nom} id="nom" name="nom" placeholder="Nom" onChange={this.handleChange} />
                                    </CFormGroup>
                                </CCol>
                                <CCol xs="6">
                                    <CFormGroup>
                                        <CInput value={this.state.prenom} id="prenom" name="prenom" placeholder="Prenom"  onChange={this.handleChange} />
                                    </CFormGroup>
                                </CCol>
                            </CRow>
                            <CRow>
                                <CCol xs="12">
                                    <CFormGroup>
                                        <CInput value={this.state.email} id="email" name="email" placeholder="Email de l'utilisateur"  onChange={this.handleChange} />
                                    </CFormGroup>
                                </CCol>
                            </CRow>
                            <CRow>
                                <CCol xs="4">
                                    <CFormGroup>
                                        <CSelect custom value={this.state.pays} name="pays" id="pays"  onChange={this.handleChange}>
                                            <option disabled selected>Choisir ton pays</option>
                                            <Pays />
                                        </CSelect>
                                    </CFormGroup>
                                </CCol>
                                <CCol xs="8">
                                    <CFormGroup>
                                        <CInput value={this.state.numero} name="numero" id="numero" placeholder="numero"  onChange={this.handleChange} />
                                    </CFormGroup>
                                </CCol>
                            </CRow>
                            <CRow>
                                <CCol xs="12">
                                    <CFormGroup>
                                        <CSelect custom name="periode" id="periode" value={this.state.periode} onChange={this.handleChange}>
                                            <option disabled selected>Selectionnez une période</option>
                                            <option value="1">un mois</option>
                                            <option value="2">deux mois</option>
                                            <option value="3">Inconnu</option>
                                        </CSelect>
                                    </CFormGroup>
                                </CCol>
                            </CRow>
                            <CRow>
                                <CCol xs="12">
                                    <CFormGroup>
                                        <CSelect custom name="type" id="type" value={this.state.type} onChange={this.handleChange}>
                                            <option disabled selected>Selectionnez le type de projet</option>
                                            <option value="1">azer</option>
                                            <option value="2">tyuiop</option>
                                            <option value="2">qwer</option>
                                        </CSelect>
                                    </CFormGroup>
                                </CCol>
                            </CRow>
                            <CRow>
                                <CCol xs="12">
                                    <CButton type="submit" size="xs" color="info"><i className="fe fe-check-circle"></i> Submit</CButton>
                                </CCol>
                            </CRow>
                            <CRow>
                                <CCol xs="12">
                                    <br />
                                </CCol>
                            </CRow>
                            <CRow>
                                <CCol xs="12">
                                        {this.state.nomEmpty}
                                        {this.state.prenomEmpty}
                                        {this.state.emailEmpty}
                                        {this.state.paysEmpty}
                                        {this.state.numeroEmpty}
                                        {this.state.periodeEmpty}
                                        {this.state.typeEmpty}
                                </CCol>
                            </CRow>
                        </CCol>
                        <CCol sm="5">
                            <br /><br /><br /><br />
                            <CCard>
                                <CCardHeader>
                                    Nos Contacts
                                </CCardHeader>
                                <CCardBody>
                                    <CFormGroup>
                                        <CLabel htmlFor="company">Numéro : </CLabel>
                                        <br />
                                        <CLabel htmlFor="company" className="text-info">(+237) 695 346 925</CLabel>
                                    </CFormGroup>
                                    <CFormGroup>
                                        <CLabel htmlFor="company">Email : </CLabel>
                                        <br />
                                        <CLabel htmlFor="company" className="text-info">  ismael@gmail.com</CLabel>
                                    </CFormGroup>
                                    <CFormGroup>
                                        <CLabel htmlFor="company">Adresse</CLabel>
                                        <br />
                                        <CLabel htmlFor="company" className="text-info">Odza - auberge bleue</CLabel>
                                    </CFormGroup>
                                </CCardBody>
                            </CCard>
                        </CCol>
                    </CRow>
                    {/*JSON.stringify(this.state)*/}
                    </form>
                </div>
                
        )
    }
}

export default Contact
